-- Отключение стандартных звуков сиреныsetWorldSoundEnabled(17, 10, false) setWorldSoundEnabled(17, 11, false)local function onVehicleSirens(key) 	local VEHICLE = getPedOccupiedVehicle(localPlayer)	if not POLICE_VEHICLE[getElementModel(VEHICLE)] then return end	if (string.upper(key) == string.upper(KEY_QUACK)) then 		-- крякалка		if not getElementData(VEHICLE, "sirens.QUACK_STATE") then			setElementData(VEHICLE, "sirens.QUACK_STATE", true)		else			setElementData(VEHICLE, "sirens.QUACK_STATE", false)		end		return 	end	if (string.upper(key) == string.upper(KEY_SIRENS)) then 		-- сирена		if not getElementData(VEHICLE, "sirens.SOUND_STATE") then			setElementData(VEHICLE, "sirens.SOUND_STATE", true)			if getVehicleSirensOn(VEHICLE) then return end		else			setElementData(VEHICLE, "sirens.SOUND_STATE", false)		end	end	if (string.upper(key) == string.upper(KEY_LIGHTS)) then 		-- мигалки		if getElementData(VEHICLE, "sirens.SOUND_STATE") then			setElementData(VEHICLE, "sirens.SOUND_STATE", false)		end	end	triggerServerEvent("sirens.setVehicleFlashers", root, VEHICLE)endlocal function unbindVehicleKey()	toggleControl("horn", true)	unbindKey(KEY_LIGHTS, "down", onVehicleSirens)	unbindKey(KEY_SIRENS, "down", onVehicleSirens)	unbindKey(KEY_QUACK , "down", onVehicleSirens)	unbindKey(KEY_QUACK , "up", onVehicleSirens)endaddEventHandler("onClientPlayerVehicleEnter", root, function(vehicle, seat)	if source == localPlayer then 		if not POLICE_VEHICLE[getElementModel(vehicle)] or seat ~= 0 then return end		toggleControl("horn", false)		bindKey(KEY_LIGHTS, "down", onVehicleSirens)		bindKey(KEY_SIRENS, "down", onVehicleSirens)		bindKey(KEY_QUACK , "down", onVehicleSirens)		bindKey(KEY_QUACK , "up", onVehicleSirens)		--outputDebugString("LOL")		--if POLICE_RADIO then startPoliceRadio() end	endend)addEventHandler("onClientPlayerVehicleExit", root, function(vehicle, seat)	if source == localPlayer then 		if not POLICE_VEHICLE[getElementModel(vehicle)] or seat ~= 0 then return end		unbindVehicleKey()		-- отключить крякалку		if getElementData(vehicle, "sirens.QUACK_STATE") then 			setElementData(vehicle, "sirens.QUACK_STATE", false)		end		if POLICE_RADIO then stopPoliceRadio() end	endend)addEventHandler("onClientElementDestroy", root, function ()	if getElementType(source) == "vehicle" then		if not POLICE_VEHICLE[getElementModel(source)] then return end		if getPedOccupiedVehicle(getLocalPlayer()) == source then			unbindVehicleKey()			--if POLICE_RADIO then stopPoliceRadio() end		end		-- отключить крякалку		if getElementData(source, "sirens.QUACK_STATE") then 			setElementData(source, "sirens.QUACK_STATE", false)		end		-- мигалки		if getElementData(source, "sirens.SOUND_STATE") then			setElementData(source, "sirens.SOUND_STATE", false)		end	endend)addEventHandler ("onClientPlayerWasted", localPlayer, function()	local VEHICLE = getPedOccupiedVehicle(source)	if not VEHICLE then return end	if not POLICE_VEHICLE[getElementModel(VEHICLE)] then return end	--if POLICE_RADIO then stopPoliceRadio() end	unbindVehicleKey()end)